import React, { useEffect, useState } from "react";
import { Text, View, StyleSheet, TextInput, TouchableOpacity, ScrollView } from "react-native";
import { useForm, Controller } from "react-hook-form";
import { WizardStore } from "../store";
import { Divider, Button as PaperButton, Card, MD3Colors, ProgressBar } from "react-native-paper";
import { useIsFocused } from "@react-navigation/native";
import OperationalSafetyTool from "./OperationalSafetyTool";
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Alert from "../alert";
export default function OperationalInstrument({ navigation }) {

  React.useLayoutEffect(() => {
    navigation.setOptions({
      headerLeft: () => null,
      headerTitle: 'Instruments', 
    });
    navigation.setOptions({ title: "" });
  }, [navigation]);

  const isFocused = useIsFocused();

  useEffect(() => {
    isFocused &&
      WizardStore.update((s) => {
        s.progress = "0.5";
      });

    console.log("updated state...", WizardStore.getRawState().progress);
  }, [isFocused]);

  const {
    handleSubmit,
    control,
    formState: { errors },
    setValue,
  } = useForm({
    mode: "onBlur",
    defaultValues: WizardStore.useState((s) => s),
  });

  const [formData, setFormData] = useState([
    { Description: "", Quantity: "" }
  ]);

  const FloatingButton = ({ onPress }) => {
    return (
      <TouchableOpacity style={styles.floatingButton} onPress={addRow}>
        {/* <Text style={styles.buttonText}>Button</Text> */}
          <FontAwesome   name="plus" color='white' size={20}>
          </FontAwesome>
      </TouchableOpacity>

    );
  };

  const NextButton = ({ onPress }) => {
    return (
      <TouchableOpacity style={styles.NextButton} onPress={handleSubmit(onSubmit)}>
          <FontAwesome name="chevron-right" color="white" size={20} style={styles.nextIcon} />
      </TouchableOpacity>

    );
  };

  const PreviousButton = ({ onPress }) => {
    return (
      <TouchableOpacity style={styles.PreviousButton} onPress={() => navigation.goBack()}>
          <FontAwesome name="chevron-left" color="white" size={20} style={styles.nextIcon} />
      </TouchableOpacity>
    );
  };

  const addRow = () => {
    setFormData([...formData, { description: "", quantity: "" }]);
  };

  const removeRow = (indexToRemove) => {
    const newFormData = [...formData];
    newFormData.splice(indexToRemove, 1);
    setFormData(newFormData);
  };

  const handleDescriptionChange = (index, value) => {
    const newFormData = [...formData];
    newFormData[index].description = value;
    setFormData(newFormData);
  };

  const handleQuantityChange = (index, value) => {
    const newFormData = [...formData];
    newFormData[index].quantity = value;
    setFormData(newFormData);
  };

  const [showErrorAlert, setShowErrorAlert] = useState(false);

  const onSubmit = (data) => {
    const instrumentData = formData.map((item, index) => ({
      Description: data[`description${index}`],
      Quantity: data[`quantity${index}`],
    }))
    .filter((item) => 
    item.Description && item.Quantity &&
    item.Description.trim() !== '' && item.Quantity.trim() !== ''
  );
    // WizardStore.update((s) => {
    //   s.progress = '0.5';
    //   console.log(instrumentData.length)
    //   if (instrumentData.length > 0) {
    //     s.instrumentData = instrumentData;
    //   }
    // });
    // navigation.navigate("OperationalSafetyTool");
    if (instrumentData.length > 0) {
    WizardStore.update((s) => {
      s.progress = '0.5';
      s.instrumentData = instrumentData;
    });
    navigation.navigate("OperationalSafetyTool");
  } else {
    setShowErrorAlert(true);
  }

  };

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
    <View style={styles.container}>
      <ProgressBar
        style={styles.progressBar}
        progress={WizardStore.getRawState().progress}
        color={MD3Colors.primary60}
      />
      <View style={{ paddingHorizontal: 16 }}>
        <Alert
          visible={showErrorAlert}
          heading="Validation Error"
          message="At least one Instruments entry is required."
          onClose={() => setShowErrorAlert(false)}
        />
        <View style={styles.formEntry}>
          {formData.map((item, index) => (
            <Card key={index} style={styles.card}>
              <Card.Content>
                <View style={styles.row}>
                  <View style={styles.column}>
                    <Text style={styles.heading}>Description</Text>
                    <Controller
                      control={control}
                      name={`description${index}`}
                      defaultValue={item.description}
                      rules={{ required: 'Description is required' }}
                      render={({ field }) => (
                        <>
                        <TextInput
                          {...field}
                          // placeholder={`Description ${index + 1}`}
                          placeholder={`Description`}
                          value={field.value}
                          onChangeText={(value) => {
                            field.onChange(value);
                            handleDescriptionChange(index, value);
                          }}
                        />
                        {errors[`description${index}`] && (
                          <Text style={{ color: 'red',paddingTop:2, fontSize:12 }}>
                            {errors[`description${index}`].message}
                          </Text>
                        )}
                        </>
                      )}
                    />
                  </View>
                  <View style={styles.column}>
                    <Text style={styles.heading}>Quantity</Text>
                    <Controller
                      control={control}
                      name={`quantity${index}`}
                      defaultValue={item.quantity}
                      rules={{ required: 'Quantity is required' }}
                      render={({ field }) => (
                        <>
                        <TextInput
                          {...field}
                          placeholder={`Quantity`}
                          value={field.value}
                          onChangeText={(value) => {
                            field.onChange(value);
                            handleQuantityChange(index, value);
                          }}
                        />
                        {errors[`quantity${index}`] && (
                          <Text style={{ color: 'red', paddingTop: 2, fontSize: 12 }}>
                            {errors[`quantity${index}`].message}
                          </Text>
                        )}
                      </>
                      )}
                    />
                  </View>
                </View>
              </Card.Content>
              <Card.Actions style={styles.cardActions}>
             <FontAwesome
                name="trash"
                color="#FF0000"
                size={20}
                onPress={() => {
                  removeRow(index);
                }}>
            </FontAwesome>
              </Card.Actions>
            </Card>
          ))} 
        </View>
        <Divider />
        <FloatingButton />
      </View>
      <PreviousButton />
      <NextButton />
    </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
  },
  floatingButton: {
    backgroundColor: 'green', 
    width: 50,
    height: 50,
    borderRadius: 25,
    alignItems: 'center',
    justifyContent: 'center',
    top: 10, 
    left: 305, 
    elevation: 8, 
  },
  NextButton: {
    backgroundColor: '#665208', 
    width: 50,
    height: 50,
    borderRadius: 25,
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    bottom: 20, 
    right: 20, 
    elevation: 8, 
  },
  PreviousButton: {
    backgroundColor: '#665208', 
    width: 50,
    height: 50,
    borderRadius: 25,
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    bottom: 20, 
    left: 20, 
    elevation: 8, 
  },
  cardActions: {
    position: 'fixed',
    bottom:55,
    paddingVertical: 10,
  },
  container: {
    flex: 1,
    backgroundColor:'#FBFAEB'
  },
  card: {
    marginBottom: 16,
  },
  row: {
    flexDirection: 'row',
  },
  column: {
    flex: 1,
    margin: 4,
  },
  heading: {
    fontWeight: 'bold',
  },
  button: {
    margin: 8,
  },

  progressBar: {
    marginBottom: 16,
    paddingHorizontal: 0,
  },
  formEntry: {
    margin: 3,
  },
  actionButton: {
    backgroundColor: '#665208',
    marginTop: 10 ,
    width:70, 
    height: 32,
    margin:10
  },
  titleStyle: {
    color: 'white',
    fontSize:12,
    margin:0,
    fontWeight:'bold',
    paddingHorizontal: 10,
    paddingVertical:0,
    marginHorizontal:0,
    marginVertical:5
 },
 actionIcon: { 
  paddingHorizontal: 3,
  paddingVertical:0,
  justifyContent: 'center',
  alignItems: 'center',
},
iconContainer: {
  backgroundColor: 'green',
  borderRadius: 20,
},
actionButton1: {
  alignItems:'center',
  justifyContent:'center',
  backgroundColor: 'green',
  marginTop: 10 ,
  width:30, 
  height: 30,
},
});
